  --input: 220 20% 40%;
  --ring: 200 80% 55%;
  --chart-1: 200 85% 65%;
  --chart-2: 150 65% 60%;
  --chart-3: 280 60% 65%;
  --chart-4: 35 80% 65%;
  --chart-5: 330 70% 70%;

  --shadow-2xs: 0px 1px 2px 0px hsl(220 40% 5% / 0.40);
  --shadow-xs: 0px 1px 3px 0px hsl(220 40% 5% / 0.50);
  --shadow-sm: 0px 2px 4px 0px hsl(220 40% 5% / 0.45), 0px 1px 2px -1px hsl(220 40% 5% / 0.35);
  --shadow: 0px 4px 6px 0px hsl(220 40% 5% / 0.50), 0px 2px 4px -1px hsl(220 40% 5% / 0.40);
  --shadow-md: 0px 6px 12px 0px hsl(220 40% 5% / 0.55), 0px 2px 6px -2px hsl(220 40% 5% / 0.45);
  --shadow-lg: 0px 10px 20px 0px hsl(220 40% 5% / 0.60), 0px 4px 8px -2px hsl(220 40% 5% / 0.50);
  --shadow-xl: 0px 20px 30px 0px hsl(220 40% 5% / 0.65), 0px 8px 12px -4px hsl(220 40% 5% / 0.55);
  --shadow-2xl: 0px 30px 50px 0px hsl(220 40% 5% / 0.70);

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
